package old;import java.io.File;import java.net.URL;public class Album {	private String album;	private File albumArt;		public Album(String album){		this.album = album;		albumArt = new File("defaultFile.txt");	}	public void setAlbum(String album) {		this.album = album;	}	public String getAlbum() {		return this.album;	}	public void setAlbumArt(File albumArt) {		this.albumArt = albumArt;	}	public File getAlbumArt() {		return this.albumArt;	}		public boolean equals(Object obj){				if(obj instanceof Album){			Album album = (Album) obj;			if((this.album.equalsIgnoreCase(album.getAlbum())  &&					(albumArt.equals(album.getAlbumArt())))){				return true;			}			return false;		}		return false;	}}